describe LoadingsController do

  # This should return the minimal set of attributes required to create a valid
  # Loading. As you add validations to Loading, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all loadings as @loadings" do
      loading = Loading.create! valid_attributes
      get :index
      assigns(:loadings).should eq([loading])
    end
  end

  describe "GET show" do
    it "assigns the requested loading as @loading" do
      loading = Loading.create! valid_attributes
      get :show, :id => loading.id.to_s
      assigns(:loading).should eq(loading)
    end
  end

  describe "GET new" do
    it "assigns a new loading as @loading" do
      get :new
      assigns(:loading).should be_a_new(Loading)
    end
  end

  describe "GET edit" do
    it "assigns the requested loading as @loading" do
      loading = Loading.create! valid_attributes
      get :edit, :id => loading.id.to_s
      assigns(:loading).should eq(loading)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Loading" do
        expect {
          post :create, :loading => valid_attributes
        }.to change(Loading, :count).by(1)
      end

      it "assigns a newly created loading as @loading" do
        post :create, :loading => valid_attributes
        assigns(:loading).should be_a(Loading)
        assigns(:loading).should be_persisted
      end

      it "redirects to the created loading" do
        post :create, :loading => valid_attributes
        response.should redirect_to(Loading.last)
