        assigns(:loading).should be_a_new(Loading)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Loading.any_instance.stub(:save).and_return(false)
        post :create, :loading => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested loading" do
        loading = Loading.create! valid_attributes
        # Assuming there are no other loadings in the database, this
        # specifies that the Loading created on the previous line
        # receives the :update_attributes message with whatever params are
        # submitted in the request.
        Loading.any_instance.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => loading.id, :loading => {'these' => 'params'}
      end

      it "assigns the requested loading as @loading" do
        loading = Loading.create! valid_attributes
        put :update, :id => loading.id, :loading => valid_attributes
